import { StyleSheet, View } from "react-native";
import { Button, Text } from "@rneui/themed";
import * as Font from "expo-font";
import FontAwesome from "@expo/vector-icons/FontAwesome";
import { React, useState, useCallback, useEffect, useMemo } from "react";
import { StackActions } from "@react-navigation/native";
import { SafeAreaView } from "react-native-safe-area-context";
import { ButtonGroup } from "react-native-elements";

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font));
}

var score = 0;
var userChoice = [];
export default function Question({ route, navigation }) {
  cacheFonts([FontAwesome.font]);

  const { questionsArr } = route.params;
  var questionCount = 0;

  const buttons = questionsArr[questionCount].choices;
  const [selectedIndex, setSelectedIndex] = useState();
  const [selectedIndexes, setSelectedIndexes] = useState([]);
  const questionType = questionsArr[questionCount].type;

  const onChoicePress = (index) => {
    if (questionType === "multiple-answer") {
      setSelectedIndexes(index);
    } else {
      setSelectedIndex(index);
    }
    console.log("selected index: " + index);
  };

  const onNextQuestion = () => {
    const sortedAnswers = selectedIndexes.sort((a, b) => a - b).join();
    console.log("here's the sorted Answer: " + sortedAnswers);

    if (
      questionType === "multiple-answer" &&
      sortedAnswers == questionsArr[questionCount].correct
    ) {
      score++;
      console.log("you're correct");
      userChoice.push(selectedIndexes);
    } else if (
      (questionType === "multiple-choice" || questionType === "true-false") &&
      selectedIndex === questionsArr[questionCount].correct
    ) {
      score++;
      console.log("you're correct");
      userChoice.push(selectedIndex);
    } else if (selectedIndex !== null && selectedIndexes.length > 0) {
      if (questionType === "multiple-answer") {
        userChoice.push(selectedIndexes);
      } else {
        userChoice.push(selectedIndex);
      }
      console.log("you're incorrect");
    } else {
      userChoice.push(selectedIndex);
      console.log("you're incorrect");
    }
    if (questionCount < questionsArr.length - 1) {
      questionCount++;
      navigation.replace("Question", {
        questionsArr: questionsArr.slice(questionCount),
      });
    } else {
      console.log(userChoice);
      navigation.replace("Summary", {
        score: score,
        userChoice: userChoice,
      });
    }
    return;
  };

  const selectButtonGroup = () => {
    if (questionType === "multiple-answer") {
      return (
        <ButtonGroup
          testID="choices"
          buttons={questionsArr[questionCount].choices}
          selectedIndexes={selectedIndexes}
          onPress={onChoicePress}
          selectMultiple
        />
      );
    } else {
      return (
        <ButtonGroup
          testID="choices"
          buttons={buttons}
          selectedIndex={selectedIndex}
          onPress={onChoicePress}
          multiSelect={questionType === "multiple-answer"}
        />
      );
    }
  };

  return (
    <SafeAreaView>
      <View>
        <Text>This is Home Screen</Text>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
  },
  title: {
    fontWeight: "bold",
  },
});
